import React from 'react'
import {AddPost, PostsType} from '../StateComponents/State'
import classes from './Main.module.css'
import Profile from './Profile/Profile'

type MainPropsType = {
    posts: Array<PostsType>
    addPropsCallback:(postText: string) => void
}

const Main: React.FC<MainPropsType> = (props) => {

    let NewPost = React.createRef<HTMLTextAreaElement>()

    const AddPost = () => {

        if (NewPost.current) {
            props.addPropsCallback(NewPost.current.value)
        }

    }

    return (
        <main className={classes.container}>
         <Profile posts={props.posts} addPropsCallback={props.addPropsCallback}/>
        </main>
    )
}



const HelloMessage = () => {
    let NewPost = React.createRef<HTMLTextAreaElement>()

    const AddPost = () => {

        if (NewPost.current) {
            props.addPropsCallback(NewPost.current.value)
        }

    }

    return (
        <div><textarea ref={NewPost} name="textPost" id="" ></textarea><button onClick={AddPost}>опубликовать</button></div>
    )

}

export default Main
