import React, {createRef} from 'react'
import Post from './Posts/Posts'
import classes from './Profile.module.css'
import {PostsType} from "../../StateComponents/State";

type ProfileTypes = {
    posts: Array<PostsType>
}

const Profile = (props: ProfileTypes) => {

    let NewPost = createRef()

    const AddPost = () => {
        alert(NewPost)
    }

    return (
        <>
            <div className={classes.infoBlock}>
                <div>Avatar</div>
            </div>

            <div>
                My posts
                <div><textarea ref={AddPost} name="textPost" id="" ></textarea><button onClick={AddPost}>опубликовать</button></div>
                {props.posts.map( p => <Post id={p.id} name={p.name} img={p.img} likes={p.likes} />)}
            </div>
        </>
    )
}

export default Profile
