import React from 'react'
import classes from './Dialogs.module.css'
import {DialogsItem} from './DialogsItem/DialogsItem'
import {DialogsType} from "../StateComponents/State";

type DialogsStateType ={
    dialogs: Array<DialogsType>
}

const Dialogs: React.FC<DialogsStateType> = (props) => {
    return (
        <div className={classes.Dialogs}>

            <div className={classes.dialogsBlock}>
                <ul className={classes.dialogsItem}>
                    {props.dialogs.map(d => {
                        return(
                            <DialogsItem id={d.id} name={d.name}/>
                        )
                    })}
                </ul>
                <div className={classes.dialogsMessage}>
                    <div>Avatar</div>
                    <div>Text message</div>
                </div>
            </div>

        </div>
    )
}

export default Dialogs
